'use strict';

module.exports = require('./lib/express');

var express = require("express");
var app = express();
const port = 3000;

app.use(express.static("public"));

app.set("view engine", "ejs");

app.get("/", (req, res)=>{
   res.render("homePage.ejs"); 
});

app.listen(port, ()=> {
	console.log(`A server is listening to you! \nPort: ${port}`);
});
